                #{pprice,                     mode=IN,  jdbcType=NUMERIC},
	    	#{outerr,                     mode=OUT, jdbcType=INTEGER},
	    	#{outmsg,                     mode=OUT, jdbcType=VARCHAR}
            )
        }

    	{ CALL items_ondelete(
	    	#{pid,                        mode=IN,  jdbcType=BIGINT},
	    	#{outerr,                     mode=OUT, jdbcType=INTEGER},
	    	#{outmsg,                     mode=OUT, jdbcType=VARCHAR}
            )
        }

